import React, {useEffect,useState} from 'react';
import makeupData from '../SiteData/makeupInfo';

function Article(props){
  const [article, setArticle] = useState({});//set as empty object
  const [cf,setCF] = useState(false);
  const cfValue = "cruelty free";
  const routeId = props.match.params.id;
  console.log(routeId);
  /*function isCrueltyFree({brandChosen}){
    if (cfValue in article.tag_list){
      setCF(true);
    }
  }*/
  useEffect(() => {
    for(let i = 0; i<makeupData.length;i++){
      if(makeupData[i].brand === routeId){
        setArticle(makeupData[i])
      }
      else{
        console.log('nope')
      }
    }
  },[]);
  console.log('article',article);
  var isCF = false
  useEffect(() => {
    for(let i = 0; i<makeupData.length;i++){
      console.log('is included',(makeupData[i].tag_list.includes("cruelty free")))
        if((makeupData[i].tag_list.includes("cruelty free")) == true){
          setCF(true)
      }
      if((makeupData[i].tag_list.includes("cruelty free")) == false){
        setCF(false)
    }
    }
  },[]);
  console.log('cf',cf);
  var desc = '';
  if(cf === true){
    desc = "This makeup brand is Cruelty Free"
  }
  if(cf === false){
    desc = "This makeup brand is NOT Cruelty Free"
  }

  return(
    <div className="article">
      <div>
          <h1 style={{textAlign:"center",paddingTop: "30px"}}>{article.brand}</h1>
          <h3 style={{textAlign:"center"}}>{desc}</h3>
      </div>

    </div>
  );

}
export default Article;

