import React, {useEffect, useState} from 'react';
import axios from 'axios';

import '../../App.css';

import brandAssessment from '../../components/brandAssessment/index.js';
import adoptionPrompt from '../../components/adoptionPrompt/index.js';

const apiKey = '_eni_p7W6MtCsH3fbnf8';

export default function Home(props){
  console.log('this is my prop:',props);
  const [error,isError] = useState(false)
  const[errorMessage, setErrorMessage] = useState('')
  const [success, isSuccess] = useState(false)

  const [lotData, setLOTData] = useState({});
  const [ans, setAns] = useState('');



  function queryLOTAPI(queryData){

    axios.get(`https://the-one-api.herokuapp.com/v1/book`)

    .then(function(response){
      console.log('response', response);
      if(response.status !== 200){
        isError(true);
        setErrorMessage(`${response.status}: ${'Error'}`);
      }
      else{
        isSuccess(true);
      }
      setLOTData(response);
      return response;
    })

    .catch(function(error){
      console.log('error', error);
      return error;
    });
  }

useEffect(() => {
  const urlParams = new URLSearchParams(props.location.search)
  console.log(urlParams)
  const dataParam = urlParams.get('fields') ? urlParams.get('locationPostalCode') : '11373';
  setAns(dataParam);
  queryLOTAPI(dataParam);
},[]);

  return(
    <div className = "App">

      {error && <div className = "errorMessage">{errorMessage}</div>}
      {success && <div className = "successMessage">Rescue query successful!</div>}

       <h1> City you selected: <span>{ans}</span></h1>
    </div>
  );

}
