import React, {useEffect,useState} from 'react';
import makeupData from '../SiteData/makeupInfo';
import org from '../SiteData/ldHigmVe_orgs_1.json';

function Article(props){
  const [article, setArticle] = useState({});//set as empty object
  const [cf,setCF] = useState(false);
  const cfValue = "cruelty free";
  const routeId = props.match.params.id;
  console.log(routeId);

  useEffect(() => {
    for(let i = 0; i<makeupData.length;i++){
      if(makeupData[i].brand === routeId){
        setArticle(makeupData[i])
        setCF(makeupData[i].tag_list.includes(cfValue))
      }
      else{
        console.log('nope')
      }
    }
  },[]);
  console.log('article',article);

  console.log('cf',cf);
  var desc = '';
  var paragraph = '';
  if(cf === true){
    desc = "This makeup brand is Cruelty Free"
    paragraph = "Unfortunately, this makeup brand is NOT cruelty-free. This means that this brand does not adhere to cruelty-free practices and therefore affects innocent animals. To do your part in helping these animals, enter your zipcode for inforamtion catered to you:"
  }
  if(cf === false){
    desc = "This makeup brand is NOT Cruelty Free"
    paragraph = "Congradulations, this makeup brand is cruelty-free! This means that this brand adheres to cruelty-free practices and therefore protects innocent animals. To keep doing your part in helping these animals, enter your zipcode for inforamtion catered to you:"
  }


  return(
    <div className="article">
      <div>
          <h1 style={{textAlign:"center",paddingTop: "30px"}}>{article.brand}</h1>
          <h3 style={{textAlign:"center"}}>{desc}</h3>
          <p style={{textAlign:"center"}}>{paragraph}</p>
      </div>

    </div>
  );

}
export default Article;

