import React, {useEffect,useState} from 'react';
import makeupData from '../SiteData/makeupInfo';

function Article(props){
  const [article, setArticle] = useState({});//set as empty object
  const [cf,setCF] = useState(false);

  const routeId = props.match.params.id;
  console.log(routeId);
  function isCrueltyFree({brandChosen}){
    for(let i = 0; i<article.tag_list; i++){
      if (article.tag_list[i]){
        setCF(true);
      }
    }
  }
  useEffect(() => {
    for(let i = 0; i<makeupData.length;i++){
      if(makeupData[i].brand === routeId){
        setArticle(makeupData[i])
        isCrueltyFree(makeupData[i])
      }
      else{
        console.log('nope')
      }
    }
  },[]);

  var desc = '';
  console.log('article',article);

  return(
    <div className="article">
      <div>
          <h1 style={{textAlign:"center",paddingTop: "30px"}}>{article.brand}</h1>
          <h3 style={{textAlign:"left", paddingBottom: "30px", marginLeft:"30%", marginRight:"30%"}}>{article.cf}</h3>
      </div>

    </div>
  );

}
export default Article;
